import React, { useEffect, useState } from 'react';

function App() {
  const [loading, setLoading] = useState(true);
  const [movies, setMovies] = useState([]);

  const getMovies = async () => {
    const json = await (
      await fetch(`https://yts.mx/api/v2/list_movies.json?mininum_rating=8.5&sort_by=year`)
    ).json();
    setMovies(json.data.movies);
    setLoading(false);
  };

  useEffect(() => {
    getMovies();
  }, []);

  return <div>
    {
      loading ?
        <h1>Loading....</h1> :
        <div>
          {movies.map(movie =>
            <div key={movie.id}>
              <h3>{movie.title}</h3>
            </div>
          )}
        </div>
    }
  </div>
}

export default App;
