import React, { useState } from "react";

function MinutesToHours() {
  const [number, setNumber] = useState('');
  const [inverted, setInverted] = useState(false);

  const onChange = (event) => setNumber(event.target.value);
  const reset = () => setNumber('');
  const onInvert = () => {
    setInverted(current => !current);
    reset();
  };

  return (
    <div>
      <div>
        <label htmlFor="Minutes">Minutes </label>
        <input
          id="Minutes"
          placeholder="Minutes"
          type="number"
          value={inverted ? number * 60 : number}
          onChange={onChange}
          disabled={inverted === true}
        />
      </div>
      <div>
        <label htmlFor="Hours">Hours </label>
        <input
          id="Hours"
          placeholder="Hours"
          type="number"
          value={inverted ? number : Math.round(number / 60)}
          onChange={onChange}
          disabled={inverted === false}
        />
      </div>
      <button onClick={reset}>Reset</button>
      <button onClick={onInvert}>{inverted ? 'Turn back' : 'Inverted'}</button>
    </div>
  )
}

function KilometersToMiles() {
  return (
    <div>
      <label htmlFor="miles">Miles </label>
      <input
        id="miles"
        placeholder="Miles"
        type="number"
      />

    </div>
  );
}

function Converter() {
  const [index, setIndex] = useState(0);
  const onSelect = (event) => {
    setIndex(event.target.value);
  }

  return (
    <div>
      <h1>Super Converter</h1>
      <select value={index} onChange={onSelect}>
        <option value="0"></option>
        <option value="1">
          Minutes & Hours
        </option>
        <option value="2">
          Kilometers & Miles
        </option>
      </select>
      {index === 1 ? <MinutesToHours /> : null}
      {index === 2 ? <KilometersToMiles /> : null}
    </div>
  );
}

export default Converter;
