import React, { useState, useEffect } from 'react';

function UseEffect() {
  const [counter, setCounter] = useState(0);
  const [keyword, setKeyword] = useState("");

  const onClick = () => setCounter(prev => prev + 1);
  console.log('I run all the time');
  const iRunOnlyOnce = () => console.log('I run Only Once');
  const onChange = (event) => setKeyword(event.target.value);

  // useEffect(iRunOnlyOnce, []);
  useEffect(() => {
    console.log('Call the API...');
  }, []);

  useEffect(() => {
    if (keyword !== "" && keyword.length > 5) {
      console.log('SEARCH FOR', keyword);
    }
  }, [keyword]);

  // console.log('Search for ', keyword)

  return <div>
    <h1>useEffect</h1>
    <h3>{counter}</h3>
    <button style={{ fontSize: "20px" }} onClick={onClick}>Click 😎</button>
    <h3>Search Practice</h3>
    <input
      type="text"
      placeholder="Search here..."
      value={keyword}
      onChange={onChange}
    />
  </div>
}

// 📘 NOTE
// - 두 개의 argument를 가지는 함수
// - 첫 번째 argument는 우리가 딱 한번만 실행하고 싶은 코드를 넣는다.
// - 두 번째는 [] -> 배열을 넣어준다.
// - useEffect가 componenet의 첫 번째 렌더 시점에 iRunOnlyOnce함수를 호출하고
// - 상태를 변화시키면 더이상 iRunOnlyOnce는 호출되지 않는다.
// - 비어있는 배열을 사용하는 것은 API같은 것들을 한 번만 호출하고 싶을 때 사용한다. 
//   배열이 비어 있기 때문에 react가 지켜볼게 없기 때문에 한 번만 실행 시키는 것
// - 배열에 넣은 요소에 변화가 있을 때만 해당 코드를 실행시킬 수 있는 방법이다.


export default UseEffect;
