import React from "react";

function Btn({ text }) {
  return <button style={{
    backgroundColor: "tomato",
    color: "white",
    border: "none",
    padding: "10px 25px",
    borderRadius: "10px",
    marginRight: "10px",
    fontSize: big ? 14 : 20,
  }}>{text}</button>
}

function Buttons() {
  return (
    <div>
      <h1>Buttons</h1>
      <Btn text="Save" big={true} />
      <Btn text="Confirm" big={false} />
    </div>
  );
}

// NOTE!
// - props는 부모 컴포넌트로 부터 자식 컴포넌트에게 데이터를 전송하는 방식이다.
// - 부모에 props를 사용하면 자식 컴포넌트(함수)의 인자로 object가 들어가게 된다.
// - 예전에는 function Btn(props) { {props.text} } -> 이런식으로 썼지만
//   현재는 function Btn({text}) { {text} } -> 이렇게 바로 중괄호를 열어 property를 받아온다.

export default Buttons;
