import { useCallback, useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
// import MovieDetail from '../components/MovieDetail'

function Detail() {
  const { id } = useParams();
  const [movie, setMovie] = useState({});
  const getMovie = useCallback(async () => {
    const json = await (
      await fetch(`https://yts.mx/api/v2/movie_details.json?movie_id=${id}`)
    ).json();
    setMovie(json.data.movie);
    // console.log(json.data.movie.genres);
    // console.log(json)
    // 🚨 genres 부분에 쓰인 map함수에서 오류가 났는데 어떻게 해결을 해야할지 모르겠다.
    // useCallback을 사용하면 현재 파일에서 genres를 map함수로 돌리면 문제가 없지만 컴포넌트에서 받아 
  }, [id])

  useEffect(() => {
    getMovie()
  }, [getMovie]);

  return <div>
    <img src={movie.background_image_original} alt={movie.title} />
    <img src={movie.medium_cover_image} alt={movie.title} />
    <h1>{movie.title}</h1>
    <span>{movie.rating}</span>
    <span> / </span>
    <span>{movie.runtime}min</span>
    <ul>
      {movie.genres.map(g => <li key={g}>{g}</li>)}
    </ul>
    <p>{movie.description_full}</p>
    {/* <MovieDetail
      title={movie.title_long}
      bgImg={movie.background_image_original}
      coverImg={movie.medium_cover_image}
      rating={movie.rating}
      runtime={movie.runtime}
      genres={movie.genres}
      summary={movie.description_full}
    /> */}
  </div>
}

export default Detail;
