import React from "react";

function MovieDetail({ title, longTitle, bgImg, coverImg, rating, runtime, genres, summary }) {
  return <div>
    <img src={bgImg} alt={title} />
    <img src={coverImg} alt={title} />
    <h1>{longTitle}</h1>
    <span>{rating}</span>
    <span> / </span>
    <span>{runtime}min</span>
    {genres.map(g =>
      (<span>{g}</span>)
    )}
    <p>{summary}</p>
  </div>
}

export default MovieDetail;
