import React, { useState, useEffect } from 'react';

function UseEffect() {
  const [counter, setCounter] = useState(0);
  const onClick = () => setCounter(prev => prev + 1);
  return <div>
    <h1>useEffect</h1>
    <h3>{counter}</h3>
    <button onClick={onClick}>+1</button>
  </div>
}

export default UseEffect;
